return items.filter(item => {
  const title = item.json.title.toLowerCase();
  return (
    title.includes("engineer") &&
    title.includes("london") &&
    !title.includes("software") &&
    !title.includes("ref:") &&
    !title.includes("cabling") &&
    !title.includes("Full Stack Engineer")
  );
});

const existingJobIds = $items("Check Job Ids").map(i => (i.json.jobId || "").trim());

return $items("Extract Job Postings").filter(item => {
  const link = item.json.link || "";
  const match = link.match(/\d+$/);   // extract last number at end of URL
  const jobId = match ? match[0] : null;

  if (!jobId) return false; // skip if jobId not found
  return !existingJobIds.includes(jobId);
});

